import React from 'react';

const ScheduleTable = ({ schedule, isEditMode, onEdit, onDelete }) => {
  const daysOfWeek = ['Понедельник', 'Вторник', 'Среда', 'Четверг', 'Пятница', 'Суббота'];

  return (
    <table className="table table-bordered">
      <thead>
        <tr>
          <th>Время</th>
          {daysOfWeek.map(day => (
            <th key={day}>{day}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {schedule.map((timeSlot, index) => (
          <tr key={index}>
            <td>{timeSlot.time}</td>
            {daysOfWeek.map(day => (
              <td key={day}>
                {timeSlot[day] ? (
                  <div>
                    <div>Группа: {timeSlot[day].group}</div>
                    <div>Предмет: {timeSlot[day].subject}</div>
                    {isEditMode && (
                      <div>
                        <button className="btn btn-sm btn-warning me-2" onClick={() => onEdit(day, index)}>Редактировать</button>
                        <button className="btn btn-sm btn-danger" onClick={() => onDelete(day, index)}>Удалить</button>
                      </div>
                    )}
                  </div>
                ) : isEditMode ? (
                  <button className="btn btn-sm btn-success" onClick={() => onEdit(day, index)}>Добавить</button>
                ) : (
                  'Нет занятий'
                )}
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default ScheduleTable;
