import React, { useState } from 'react';
import Header from '../components/Header';
import ScheduleTable from '../components/ScheduleTable';

const AdminPage = ({ onLogout }) => {
  const [isEditMode, setIsEditMode] = useState(false);
  const [schedule, setSchedule] = useState([
    { time: '08:00-09:00', Понедельник: null, Вторник: null, Среда: null, Четверг: null, Пятница: null, Суббота: null },
    { time: '09:00-10:00', Понедельник: null, Вторник: null, Среда: null, Четверг: null, Пятница: null, Суббота: null },
    // Add more time slots as needed
  ]);

  const toggleEditMode = () => {
    setIsEditMode(!isEditMode);
  };

  const handleEdit = (day, index) => {
    const group = prompt('Введите номер группы:');
    const subject = prompt('Введите название предмета:');
    const newSchedule = [...schedule];
    newSchedule[index][day] = { group, subject };
    setSchedule(newSchedule);
  };

  const handleDelete = (day, index) => {
    const newSchedule = [...schedule];
    newSchedule[index][day] = null;
    setSchedule(newSchedule);
  };

  return (
    <div className="container">
      <Header onLogout={onLogout} />
      <h2>Admin Schedule</h2>
      <button className="btn btn-secondary mb-3" onClick={toggleEditMode}>
        {isEditMode ? 'View Mode' : 'Edit Mode'}
      </button>
      <ScheduleTable schedule={schedule} isEditMode={isEditMode} onEdit={handleEdit} onDelete={handleDelete} />
    </div>
  );
};

export default AdminPage;
