import axios from 'axios';

const handleLogin = async (login, password) => {
  try {
    const response = await axios.post('http://localhost:5000/api/auth/login', {
      login: 'admin', // Corrected: 'admin' should be a string
      password: 'admin123' // Corrected: 'admin123' should be a string
    });
    console.log('Login successful:', response.data);
    // Perform additional actions after successful login if needed
  } catch (error) {
    console.error('Login error:', error);
    // Handle login errors, e.g., display an error message on the screen
  }
};

export default handleLogin; // Export the function if it's used in another file
