import React from 'react';

const TeacherPage = ({ onLogout }) => {
  return (
    <div className="container">
      <Header onLogout={onLogout} />
      <h2>Teacher Schedule</h2>
      <ScheduleTable />
    </div>
  );
};

const StudentPage = ({ onLogout }) => {
  return (
    <div className="container">
      <Header onLogout={onLogout} />
      <h2>Student Schedule</h2>
      <ScheduleTable />
    </div>
  );
};

const AdminPage = ({ onLogout }) => {
  const [isEditMode, setIsEditMode] = useState(false);

  const toggleEditMode = () => {
    setIsEditMode(!isEditMode);
  };

  return (
    <div className="container">
      <Header onLogout={onLogout} />
      <h2>Admin Schedule</h2>
      <button className="btn btn-secondary" onClick={toggleEditMode}>
        {isEditMode ? 'View Mode' : 'Edit Mode'}
      </button>
      <ScheduleTable isEditMode={isEditMode} />
    </div>
  );
};

export { TeacherPage, StudentPage, AdminPage };
