import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import axios from 'axios';
import LoginPage from './pages/LoginPage';
import AdminPage from './pages/AdminPage';
import StudentPage from './pages/StudentPage';
import TeacherPage from './pages/TeacherPage';
import EditModePage from './pages/EditModePage';

const App = () => {
  const [user, setUser] = useState(null);

  const handleLogin = async (login, password) => {
    try {
      const response = await axios.post('http://localhost:5000/routes/auth', { login, password });
      setUser(response.data);
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        {user && user.role === 'admin' && (
          <>
            <Route path="/admin" element={<AdminPage user={user} />} />
            <Route path="/edit" element={<EditModePage user={user} />} />
          </>
        )}
        {user && user.role === 'teacher' && (
          <Route path="/teacher" element={<TeacherPage user={user} />} />
        )}
        {user && user.role === 'student' && (
          <Route path="/student" element={<StudentPage user={user} />} />
        )}
        <Route path="/" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
